import React, { useState, useEffect } from 'react';
import { Events } from './components/Events'
import { Form } from './components/Form'
import { NameModal } from './components/NameModal'

export default function App(){
  const [username, setUserName] = useState('')
  const [websocket, setWebsocket] = useState(null); 
  const [messages, setMessages] = useState([]);

  const handleSaveName = (name) => {
    setHandleName(name);
  }

  useEffect(() => {
    var ws = new WebSocket('ws://localhost:5990/ws')
    console.log('trying to connect to ws')
    ws.onopen = () => {
      console.log("Websocket is connected")
    };

    ws.onmessage = (e) => {
      const message = e.data;
      console.log("Message from server: ", message);
      console.log(messages);
      setMessages((prevMessages) => [...prevMessages, message]);
    };

    ws.onerror = (e) => {
      console.log("WS error: ", e);
    };

    ws.onclose = (e) => {
      console.log("websocket is closed");
    };

    setWebsocket(ws);

    return () => {
      ws.close();
    };
  }, [username]);

  return (
  <div class="flex flex-col h-screen">
    { !username ? <NameModal onSaveName={handleSaveName} /> :
      (
      <div>
      <h3 className="text-center bg-gray-100 text-2xl font-bold">Chat App</h3>
      <div class="flex-grow overflow-auto">
        <Events messages={messages} />
      </div>
      <div class="p-4 bg-gray-100 w-full fixed bottom-0 left-0">
        <Form websocket={websocket} />
      </div>
      </div>
      )
    }
  </div>
  );
}
