import React, { useState } from 'react';

export function Form({ websocket }){
  const [value, setValue] = useState('');

  function onSubmit(e){
    e.preventDefault();
    if (websocket && websocket.readyState === WebSocket.OPEN){
      websocket.send(value);
    }
    else{
      console.error("Websocket is not open")
    }
    setValue('');
  }

  return (
    <form onSubmit={ onSubmit }>
      <div class="relative">
        <input class="block w-full p-4 ps-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
        onChange={ e => setValue(e.target.value) } />
        <button className="px-3 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
        type="submit">Submit</button>
      </div>
    </form>
  );
}
